import axios from 'axios';
import { useState, useEffect } from 'react';
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom';

import NewsCard from './components/NewsCard/NewsCard';
import NewDetails from './components/NewDetails/NewDetails'
import Login from './components/Login/Login';
import Header from './components/Header/Header';

import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';

import './App.css';

function App() {

  const [news, setNews] = useState([]);

  useState(() => {
    axios.get('http://localhost:3003/news')
      .then((response) => {
        setNews(response.data);
      }).catch((err) => {
        console.log(`Erro: ${err}`);
      })
  });

  return (
    <BrowserRouter>

      <div className="App">
        <Header />

        <Switch>
          <Redirect from='/' to='/news' exact />
          <Route path="/news" exact>
            <NewsCard key={props.idNew} news={news}></NewsCard>
          </Route>

          <Route path="/news/:id" exact>
            <NewDetails />
          </Route>

          <Route path="/login" exact>
            <Login />
          </Route>

          <Route render={() => <div> Pagina não encontrada </div>} />
        </Switch>
      </div>

    </BrowserRouter>
  );
}

export default App;
